const HomeScreen = ({navigation}) => {
  // Check notification permissions when component mounts
  useEffect(() => {
    // Check permissions and request if needed
    const checkAndRequestPermissions = async () => {
      const hasPermission = await checkNotificationPermission();
      if (!hasPermission) {
        console.log('Requesting notification permissions...');
        await requestNotificationPermission();
      }
    };

    checkAndRequestPermissions();
  }, []);

  async function whereToGo() {
    navigation.navigate('Events'); // Navigate after the animation completes

    // Check if we have permission first
    const hasPermission = await checkNotificationPermission();

    // If no permission, request it
    if (!hasPermission) {
      const granted = await requestNotificationPermission();
      if (!granted) {
        console.log('Notification permission denied');
        // On iOS, show an alert telling users how to enable notifications
        if (Platform.OS === 'ios') {
          Alert.alert(
            'Notification Permission',
            'Please enable notifications in Settings to receive important event reminders.',
            [
              {text: 'Cancel', style: 'cancel'},
              {text: 'Settings', onPress: () => Linking.openSettings()},
            ],
          );
        }
        return;
      }
    }

    // Create notification
    try {
      // Schedule first notification for May 31, 2025 at 1:15 PM
      const firstDate = new Date(2025, 4, 31, 13, 15, 0); // May is 4 (0-indexed)
      console.log('First notification scheduled for:', firstDate.toString());

      // Schedule second notification for June 1, 2025 at 9:00 AM
      const secondDate = new Date(2025, 5, 1, 9, 0, 0); // June is 5 (0-indexed)
      console.log('Second notification scheduled for:', secondDate.toString());

      // Create time-based triggers
      const firstTrigger = {
        type: TriggerType.TIMESTAMP,
        timestamp: firstDate.getTime(),
      };

      const secondTrigger = {
        type: TriggerType.TIMESTAMP,
        timestamp: secondDate.getTime(),
      };

      // Create a trigger notification for May 31st
      await notifee.createTriggerNotification(
        {
          id: 'survey-notification-1',
          title: 'Post event survey form',
          body: 'Please remember to fill out the post event survey for your feedback!',
          android: {
            channelId: 'default-channel',
            importance: AndroidImportance.HIGH,
            pressAction: {
              id: 'default',
            },
          },
          ios: {
            sound: 'default',
            foregroundPresentationOptions: {
              badge: true,
              sound: true,
              banner: true,
              list: true,
            },
          },
        },
        firstTrigger,
      );

      // Create a trigger notification for June 1st
      await notifee.createTriggerNotification(
        {
          id: 'survey-notification-2',
          title: 'Post event survey form',
          body: 'Did you enjoy the SST Open House? Please fill out our survey!',
          android: {
            channelId: 'default-channel',
            importance: AndroidImportance.HIGH,
            pressAction: {
              id: 'default',
            },
          },
          ios: {
            sound: 'default',
            foregroundPresentationOptions: {
              badge: true,
              sound: true,
              banner: true,
              list: true,
            },
          },
        },
        secondTrigger,
      );
      console.log('Notification scheduled successfully');
    } catch (error) {
      console.error('Failed to schedule notification:', error);
    }
  }
  return (
    <View style={styles.container}>
      <ImageBackground
        source={require('./assets/landingPage.png')}
        style={[styles.imageBackground /*  {opacity: fadeAnim} */]}>
        <TouchableOpacity
          style={{
            width: '80%',
            height: '8%',
            backgroundColor: '#356AA9',
            marginTop: '158%',
            marginLeft: '10%',
            alignItems: 'center',
            justifyContent: 'center',
            flexDirection: 'row',
          }}
          onPress={() => {
            whereToGo();
            notifee
              .getTriggerNotificationIds()
              .then(ids => console.log('All trigger notifications: ', ids));
          }}>
          <Svg
            xmlns="http://www.w3.org/2000/Svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none">
            <Path
              d="M22.5 12C22.5006 12.2546 22.4353 12.5051 22.3105 12.7271C22.1856 12.949 22.0055 13.1349 21.7875 13.2666L8.28 21.5297C8.05227 21.6691 7.79144 21.7453 7.52445 21.7502C7.25746 21.7551 6.99399 21.6887 6.76125 21.5578C6.53073 21.4289 6.3387 21.241 6.2049 21.0132C6.07111 20.7855 6.00039 20.5263 6 20.2622V3.73781C6.00039 3.4737 6.07111 3.21446 6.2049 2.98675C6.3387 2.75904 6.53073 2.57107 6.76125 2.44218C6.99399 2.31126 7.25746 2.24484 7.52445 2.24978C7.79144 2.25473 8.05227 2.33086 8.28 2.47031L21.7875 10.7334C22.0055 10.8651 22.1856 11.051 22.3105 11.2729C22.4353 11.4949 22.5006 11.7453 22.5 12Z"
              fill="#F2F2F3"
            />
          </Svg>
          <Text
            allowFontScaling={false}
            style={{
              color: '#F2F2F3',
              fontFamily: 'Prototype',
              fontSize: 20,
              fontStyle: 'normal',
              fontWeight: '400',
              lineHeight: 24,
            }}>
            {'   '}
            Get Started
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() => {
            whereToGo();
            notifee
              .getTriggerNotificationIds()
              .then(ids => console.log('All trigger notifications: ', ids));
          }}
          style={{
            width: '80%',
            height: '8%',
            bottom: '14%',
            marginLeft: '8%',
            alignItems: 'center',
            justifyContent: 'center',
            flexDirection: 'row',
            borderWidth: 4,
            borderColor: '#356AA9',
          }}
        />
      </ImageBackground>
    </View>
  );
};

